/*Table structure for table `site_settings` */

DROP TABLE IF EXISTS `site_settings`;

CREATE TABLE `site_settings` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `value` text COLLATE utf8mb4_unicode_ci,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `value_type` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` text COLLATE utf8mb4_unicode_ci,
  `category` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `community_id` bigint DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `index_site_settings_on_category` (`category`),
  KEY `index_site_settings_on_community_id` (`community_id`),
  KEY `index_site_settings_on_name` (`name`),
  CONSTRAINT `fk_rails_cc5b88e039` FOREIGN KEY (`community_id`) REFERENCES `communities` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=58 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

/*Data for the table `site_settings` */

insert  into `site_settings`(`id`,`name`,`value`,`created_at`,`updated_at`,`value_type`,`description`,`category`,`community_id`) values 
(1,'SiteName','QPixel','2022-05-17 06:13:35','2022-05-17 06:13:35','string','The main name for this site. Will appear in the page title, among other places.\n','SiteDetails',1),
(2,'SiteLogoPath','/assets/logo.png','2022-05-17 06:13:35','2022-05-17 06:13:35','string','The path, relative to the root of the domain, to the site\'s logo file. Can also be an absolute URL.\n','SiteDetails',1),
(3,'MobileLogoPath',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','string','The path to a simplified, squarer version of the site logo for use on mobile or small-screen devices.\n','SiteDetails',1),
(4,'SiteCategoryHeaderDefaultColor','blue','2022-05-17 06:13:35','2022-05-17 06:13:35','string','Select a color to be used for categories that don\'t define a header color, and for category-neutral views. Valid are: turquoise, green, blue, darkblue, purple, gray, bluegray, yellow, orange, pink, red.\n','SiteDetails',1),
(5,'SiteHeaderIsDark','f','2022-05-17 06:13:35','2022-05-17 06:13:35','boolean','Whether or not the site header has a dark color. The theme can be edited in `/public/assets/community/[Community Name].css`\n','SiteDetails',1),
(6,'AllowSelfVotes','f','2022-05-17 06:13:35','2022-05-17 06:13:35','boolean','Whether or not voting for your own posts is allowed.\n','ReputationAndVoting',1),
(7,'AskingGuidance','<!--- Blurb shown to new users when creating a question until dismissed -->\n<ul>\n    <li>Your title should be a one sentence summary of your question. </li>\n    <li>Break different topics up into paragraphs. Multiple paragraphs are easier to read than a wall of text. </li>\n    <li>Use clear, simple language. Be specific. </li>\n    <li>Reread your post before saving (preferably out loud) and rewrite anything that sounds awkward. </li>\n    <li>Give context for your question. Tell us what you&#39;re trying to do and why.  </li>\n    <li>Tell us what you&#39;ve already tried. </li>\n    <li>Don&#39;t provide irrelevant information; it&#39;s a distractor from the question you want answered. </li>\n</ul>','2022-05-17 06:13:35','2022-05-17 06:13:35','text','Guidance to help users ask a good question. Will be displayed in the sidebar on the ask a question page. Supports Markdown, including a subset of HTML.\n','HelpAndGuidance',1),
(8,'AnsweringGuidance','<p>When answering, remember to...</p><ul><li><strong>answer the question</strong> - posts that don\'t address the problem clutter up the thread</li><li><strong>explain why you\'re right</strong> - not everyone knows what you do, so explain why this is the answer</li></ul></p>\n','2022-05-17 06:13:35','2022-05-17 06:13:35','text','Guidance to help users write a good answer. Will be displayed in the sidebar on the answer page. Supports Markdown, including a subset of HTML.\n','HelpAndGuidance',1),
(9,'AdministratorContactEmail','contact@example.com','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A contact address for the owners or administrators of the site. Will be displayed on error pages.\n','SiteDetails',1),
(10,'HotQuestionsCount','5','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','How many \"hot\" questions to display in the sidebar.\n','Display',1),
(11,'AdminBadgeCharacter','','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A character to display after an admin\'s username to distinguish their role.\n','Display',1),
(12,'ModBadgeCharacter','','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A character to display after a moderator\'s username to distinguish their role.\n','Display',1),
(13,'SoftDeleteTransferUser','-1','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','A user ID to which to transfer content when soft-deleting a user account.\n','AdvancedSettings',1),
(14,'NewUserInitialRep','1','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','How much reputation new users initially start with.\n','ReputationAndVoting',1),
(15,'FreeVotes','5','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','How many votes new user accounts have (actual vote count increased by reputation).\n','ReputationAndVoting',1),
(16,'SEApiClientId','','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A client ID for the Stack Exchange API, to allow users to authenticate with SE.\n','Integrations',1),
(17,'SEApiClientSecret','','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A client secret corresponding to the client ID set in SEApiClientId.\n','Integrations',1),
(18,'SEApiKey','','2022-05-17 06:13:35','2022-05-17 06:13:35','string','An API key for the Stack Exchange API.\n','Integrations',1),
(19,'ContentLicenseName','CC BY-SA 4.0','2022-05-17 06:13:35','2022-05-17 06:13:35','string','The name of the license under which content here is posted when no other is specified when posting.\n','SiteDetails',1),
(20,'ContentLicenseLink','https://creativecommons.org/licenses/by-sa/4.0/','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A link to the full legal code of the license specified in ContentLicenseName.\n','SiteDetails',1),
(21,'MaxTagLength','35','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The maximum characters a single tag name may contain. Default is 35 for compatibility with Stack Exchange; going lower may introduce validation issues with content imported from SE.\n','SiteDetails',1),
(22,'MaxTitleLength','150','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The maximum characters a post title may contain. Default is 150 for compatibility with Stack Exchange; going lower may introduce validation issues with content imported from SE.    \n','SiteDetails',1),
(23,'MaxUploadSize','2MB','2022-05-17 06:13:35','2022-05-17 06:13:35','string','The maximum size of images that users may upload. Changing this setting DOES NOT change the limit - the limit is set by your web server configuration. This setting should be set to the same value so that the correct limit can be displayed to users.\n','AdvancedSettings',1),
(24,'FirstVisitGuidance',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','text','A dismissible message that is shown to logged-out users when they visit the site, to introduce them to the basics of the site.\n','HelpAndGuidance',1),
(25,'InterestingSubscriptionScoreThreshold','1','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The minimum score a question must have to qualify for selection for the Interesting email subscription.\n','EmailSubscriptions',1),
(26,'LotteryAgeDeprecationSpeed','0.002','2022-05-17 06:13:35','2022-05-17 06:13:35','float','The relative speed at which questions age out of selection for the \"lottery\" tab. 0.002 is normal; higher values will age questions out faster.\n','AdvancedSettings',1),
(27,'JITAdvancedSearchHelp','To further refine your search, you can use additional qualifiers such as `score:>0.5`. For example, the search `score:>=0.5 created:<1y grammar` would return only posts mentioning \"grammar\" that have a score >= 0.5 and were created less than a year ago.\n','2022-05-17 06:13:35','2022-05-17 06:13:35','text','Just-in-time guidance that\'s shown on the search page to let users know about advanced search. Markdown is available here.\n','HelpAndGuidance',1),
(28,'IconPath',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','string','Path to a file to use as the site\'s favicon. Must be an .ICO file.\n','SiteDetails',1),
(29,'AllowContentTransfer','t','2022-05-17 06:13:35','2022-05-17 06:13:35','boolean','Enable transferring pre-imported content from Stack Exchange to the associated user\'s account here.\n','Integrations',1),
(30,'AnalyticsURL',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','string','Full URL to a Matomo analytics instance, with leading protocol and trailing slash.\n','Integrations',1),
(31,'AnalyticsSiteId',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','string','Site ID for this community in your Matomo analytics instance.\n','Integrations',1),
(32,'ChatLink',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','string','Link to your chat platform of choice.\n','Integrations',1),
(33,'SidebarNoticeText',NULL,'2022-05-17 06:13:35','2022-05-17 06:13:35','text','Text to display in a notice box at the top of the sidebar. Markdown allowed.\n','Display',1),
(34,'MathJaxEnabled','f','2022-05-17 06:13:35','2022-05-17 06:13:35','boolean','Enable MathJax on this site for math display. This will incur a small penalty in page loading times, so consider whether you really need it.\n','Display',1),
(35,'SyntaxHighlightingEnabled','f','2022-05-17 06:13:35','2022-05-17 06:13:35','boolean','Enable highlight.js syntax highlighting on this site. This incurs a small penalty in page loading times, so consider whether you really need it.\n','Display',1),
(36,'ScoringVariable','2','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','Variable N value to use in the scoring algorithm. DO NOT SET THIS TO ZERO, IT WILL BREAK YOUR SITE. Generally best left alone unless you understand the mathematics at play.\n','AdvancedSettings',1),
(37,'SiteAdSlogan','Try our Q&A site!','2022-05-17 06:13:35','2022-05-17 06:13:35','string','A slogan to be shown on the /ads/community.png page.\n','SiteDetails',1),
(38,'HotPostsScoreThreshold','0.6','2022-05-17 06:13:35','2022-05-17 06:13:35','float','The minimum score a question must have to qualify for selection for the Hot Posts sidebar and for being selected as random advertisement.\n','AdvancedSettings',1),
(39,'NewSiteMode','t','2022-05-17 06:13:35','2022-05-17 06:13:35','boolean','New site mode will grant some abilities more easily.\n','ReputationAndVoting',1),
(40,'RL_NewUserVotes','5','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of votes on questions, articles and answers to non-own questions new users may cast within 24h.\n','RateLimits',1),
(41,'RL_Votes','30','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of votes on questions, articles and answers to non-own questions users with the unrestricted ability may cast within 24h.\n','RateLimits',1),
(42,'RL_NewUserTopLevelPosts','3','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of questions and articles new users may post within 24h.\n','RateLimits',1),
(43,'RL_TopLevelPosts','20','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of questions and articles users with the unrestricted ability may post within 24h.\n','RateLimits',1),
(44,'RL_NewUserSecondLevelPosts','10','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of answers new users may post within 24h.\n','RateLimits',1),
(45,'RL_SecondLevelPosts','30','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of answers users with the unrestricted ability may post within 24h.\n','RateLimits',1),
(46,'RL_NewUserFlags','10','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of flags new users may raise within 24h.\n','RateLimits',1),
(47,'RL_Flags','30','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of flags users with the unrestricted ability may raise within 24h.\n','RateLimits',1),
(48,'RL_NewUserSuggestedEdits','3','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of edits new users may suggest within 24h.\n','RateLimits',1),
(49,'RL_SuggestedEdits','20','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of edits users with the unrestricted ability may suggest within 24h.\n','RateLimits',1),
(50,'RL_NewUserComments','0','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of comments new users may add on other people\'s posts within 24h.\n','RateLimits',1),
(51,'RL_Comments','50','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The amount of comments users with the unrestricted ability may add on other people\'s posts within 24h.\n','RateLimits',1),
(52,'TableOfContentsThreshold','5','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The minimum number of child posts before the table of contents is shown. If the number of child posts is less than this threshold, the TOC is not shown.\n','Display',1),
(53,'TableOfContentsExcerptLength','120','2022-05-17 06:13:35','2022-05-17 06:13:35','integer','The number of characters to show in the answer excerpt in the TOC. The TOC strips most formatting, so beware of large values -- the TOC might become hard to read.\n','Display',1),
(54,'UserCardDetails','r.12sE','2022-05-17 06:13:35','2022-05-17 06:13:35','string','Which details are to be shown on the user card. \".\" means new line, \"r\" reputation, \"p\" number of posts, \"1\" top-level posts, \"2\" second-level posts (answers), \"s\" score of votes received (up - down), \"v\" number of votes received, \"V\" number of votes cast, \"E\" number of edits made on posts.\n','Display',1),
